components.arkaios/ArkaiosKeyGenerator:
import React from 'react';
import CryptoJS from 'crypto-js';
import { Download } from 'lucide-react';
import { Button } from '@/components/ui/button';

const MASTER_KEY = "ARKAIOS_SECURE_KEY_2023!@#";

export default function ArkaiosKeyGenerator({ userData, membershipData }) {
  const generateArkaiosKey = () => {
    const timestamp = new Date().toISOString();
    const userEmail = userData.user_email || userData.email;
    const walletAddress = userData.wallet_address || `ARK-${userEmail.split('@')[0].toUpperCase()}`;

    const identityData = {
      id: walletAddress,
      email: userEmail,
      membership_status: membershipData?.status || "none",
      membership_expiry: membershipData?.end_date || null,
      membership_plan: membershipData?.plan_type || null,
      saldo: userData.balance || 0,
      linked_ids: userData.linked_ids || [],
      issued: timestamp,
      version: "2.5",
      protocol: "BITCOINLAND-BIFROST",
      signature: btoa(`ARKAIOS-SIG-${walletAddress}-${Date.now()}`)
    };

    const stringData = JSON.stringify(identityData, null, 2);
    const encryptedData = CryptoJS.AES.encrypt(stringData, MASTER_KEY).toString();

    return { identityData, encryptedData };
  };

  const downloadArkaiosKey = () => {
    const { encryptedData } = generateArkaiosKey();
    const blob = new Blob([encryptedData], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = 'identidad.arkaios';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
  };

  return (
    <Button
      onClick={downloadArkaiosKey}
      size="lg"
      className="bg-gradient-to-r from-purple-600 to-cyan-600 hover:from-purple-500 hover:to-cyan-500 text-white font-bold"
    >
      <Download className="w-5 h-5 mr-2" />
      Sincronizar Pasaporte con el Nexo
    </Button>
  );
}

components.arkaios/IdentityLinker:
import React, { useState, useEffect } from 'react';
import { base44 } from '@/api/base44Client';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Badge } from '@/components/ui/badge';
import { Link2, Mail, Wallet, Phone, MessageCircle, Twitter, CheckCircle2, Plus, Trash2, Loader2 } from 'lucide-react';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';

export default function IdentityLinker({ user, balance, onUpdate }) {
  const [linkedIds, setLinkedIds] = useState([]);
  const [isAdding, setIsAdding] = useState(false);
  const [newIdType, setNewIdType] = useState('google_email');
  const [newIdValue, setNewIdValue] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);

  useEffect(() => {
    if (balance?.linked_ids) {
      setLinkedIds(balance.linked_ids);
    }
  }, [balance]);

  const getIcon = (type) => {
    switch (type) {
      case 'google_email': return <Mail className="w-4 h-4" />;
      case 'wallet_address': return <Wallet className="w-4 h-4" />;
      case 'phone': return <Phone className="w-4 h-4" />;
      case 'telegram': return <MessageCircle className="w-4 h-4" />;
      case 'twitter': return <Twitter className="w-4 h-4" />;
      default: return <Link2 className="w-4 h-4" />;
    }
  };

  const getLabel = (type) => {
    switch (type) {
      case 'google_email': return 'Google Email';
      case 'wallet_address': return 'Wallet Address';
      case 'phone': return 'Tel칠fono';
      case 'telegram': return 'Telegram';
      case 'twitter': return 'Twitter';
      default: return type;
    }
  };

  const handleAddIdentity = async () => {
    if (!newIdValue.trim()) return;

    setIsProcessing(true);
    try {
      const newLinkedId = {
        type: newIdType,
        value: newIdValue.trim(),
        verified: true,
        linked_at: new Date().toISOString()
      };

      const updatedLinkedIds = [...linkedIds, newLinkedId];

      await base44.entities.AmrBalance.update(balance.id, {
        linked_ids: updatedLinkedIds
      });

      setLinkedIds(updatedLinkedIds);
      setNewIdValue('');
      setIsAdding(false);

      if (onUpdate) onUpdate();
    } catch (error) {
      console.error('Error adding linked identity:', error);
    } finally {
      setIsProcessing(false);
    }
  };

  const handleRemoveIdentity = async (index) => {
    setIsProcessing(true);
    try {
      const updatedLinkedIds = linkedIds.filter((_, i) => i !== index);

      await base44.entities.AmrBalance.update(balance.id, {
        linked_ids: updatedLinkedIds
      });

      setLinkedIds(updatedLinkedIds);

      if (onUpdate) onUpdate();
    } catch (error) {
      console.error('Error removing linked identity:', error);
    } finally {
      setIsProcessing(false);
    }
  };

  return (
    <Card className="bg-slate-900/50 border-purple-500/30">
      <CardContent className="p-6">
        <div className="flex items-center gap-3 mb-4">
          <Link2 className="w-6 h-6 text-purple-400" />
          <h3 className="text-xl font-bold text-white">Identidades Vinculadas</h3>
        </div>

        <p className="text-slate-400 text-sm mb-6">
          Vincula tus identidades externas para acceso unificado en todo el ecosistema ARKAIOS
        </p>

        {/* Lista de identidades vinculadas */}
        <div className="space-y-3 mb-6">
          {linkedIds.length === 0 ? (
            <div className="text-center py-8 text-slate-500 border border-slate-700/50 rounded-lg">
              <Link2 className="w-8 h-8 mx-auto mb-2 opacity-50" />
              <p className="text-sm">No hay identidades vinculadas</p>
            </div>
          ) : (
            linkedIds.map((linkedId, index) => (
              <div
                key={index}
                className="flex items-center justify-between p-3 bg-slate-800/50 rounded-lg border border-slate-700/50"
              >
                <div className="flex items-center gap-3">
                  <div className="w-10 h-10 bg-purple-500/20 rounded-lg flex items-center justify-center text-purple-400">
                    {getIcon(linkedId.type)}
                  </div>
                  <div>
                    <p className="text-white font-medium text-sm">{getLabel(linkedId.type)}</p>
                    <p className="text-slate-400 text-xs">{linkedId.value}</p>
                  </div>
                </div>
                <div className="flex items-center gap-2">
                  {linkedId.verified && (
                    <Badge className="bg-green-500/20 text-green-400 border-green-500/30">
                      <CheckCircle2 className="w-3 h-3 mr-1" />
                      Verificado
                    </Badge>
                  )}
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => handleRemoveIdentity(index)}
                    disabled={isProcessing}
                    className="text-red-400 hover:text-red-300 hover:bg-red-500/10"
                  >
                    <Trash2 className="w-4 h-4" />
                  </Button>
                </div>
              </div>
            ))
          )}
        </div>

        {/* Formulario para agregar nueva identidad */}
        {isAdding ? (
          <div className="space-y-3 p-4 bg-slate-800/30 rounded-lg border border-purple-500/30">
            <Select value={newIdType} onValueChange={setNewIdType}>
              <SelectTrigger className="bg-slate-800 border-slate-700 text-white">
                <SelectValue placeholder="Tipo de identidad" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="google_email">
                  <div className="flex items-center gap-2">
                    <Mail className="w-4 h-4" />
                    Google Email
                  </div>
                </SelectItem>
                <SelectItem value="wallet_address">
                  <div className="flex items-center gap-2">
                    <Wallet className="w-4 h-4" />
                    Wallet Address
                  </div>
                </SelectItem>
                <SelectItem value="phone">
                  <div className="flex items-center gap-2">
                    <Phone className="w-4 h-4" />
                    Tel칠fono
                  </div>
                </SelectItem>
                <SelectItem value="telegram">
                  <div className="flex items-center gap-2">
                    <MessageCircle className="w-4 h-4" />
                    Telegram
                  </div>
                </SelectItem>
                <SelectItem value="twitter">
                  <div className="flex items-center gap-2">
                    <Twitter className="w-4 h-4" />
                    Twitter
                  </div>
                </SelectItem>
              </SelectContent>
            </Select>

            <Input
              placeholder="Ingresa el valor de la identidad"
              value={newIdValue}
              onChange={(e) => setNewIdValue(e.target.value)}
              className="bg-slate-800 border-slate-700 text-white"
            />

            <div className="flex gap-2">
              <Button
                onClick={() => {
                  setIsAdding(false);
                  setNewIdValue('');
                }}
                variant="outline"
                className="flex-1 border-slate-600 text-slate-300"
                disabled={isProcessing}
              >
                Cancelar
              </Button>
              <Button
                onClick={handleAddIdentity}
                className="flex-1 bg-gradient-to-r from-purple-600 to-cyan-600 hover:from-purple-500 hover:to-cyan-500"
                disabled={isProcessing || !newIdValue.trim()}
              >
                {isProcessing ? (
                  <>
                    <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                    Vinculando...
                  </>
                ) : (
                  <>
                    <CheckCircle2 className="w-4 h-4 mr-2" />
                    Vincular
                  </>
                )}
              </Button>
            </div>
          </div>
        ) : (
          <Button
            onClick={() => setIsAdding(true)}
            className="w-full bg-purple-600 hover:bg-purple-500"
          >
            <Plus className="w-4 h-4 mr-2" />
            Vincular Nueva Identidad
          </Button>
        )}
      </CardContent>
    </Card>
  );
}

components.arkaios/MembershipDashboard:
import React, { useState, useEffect } from 'react';
import { base44 } from '@/api/base44Client';
import { Card, CardContent } from '@/components/ui/card';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Crown, Calendar, CheckCircle2, XCircle, Loader2, Wallet, History, Send, Link2 } from 'lucide-react';
import ArkaiosKeyGenerator from './ArkaiosKeyGenerator';
import TransferSystem from './TransferSystem';
import TransactionHistory from './TransactionHistory';
import IdentityLinker from './IdentityLinker';
import PassportVerifier from './PassportVerifier';
import { format } from 'date-fns';

export default function MembershipDashboard({ user }) {
  const [membership, setMembership] = useState(null);
  const [balance, setBalance] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    loadUserData();
  }, [user]);

  const loadUserData = async () => {
    setIsLoading(true);
    try {
      // Cargar membres칤a
      const memberships = await base44.entities.Membership.filter({
        user_email: user.email
      }, '-created_date', 1);

      if (memberships && memberships.length > 0) {
        const userMembership = memberships[0];

        // Verificar si est치 activa
        if (userMembership.status === 'active') {
          const endDate = new Date(userMembership.end_date);
          const now = new Date();

          if (endDate < now) {
            await base44.entities.Membership.update(userMembership.id, {
              status: 'expired'
            });
            userMembership.status = 'expired';
          }
        }

        setMembership(userMembership);
      }

      // Cargar balance
      const balances = await base44.entities.AmrBalance.filter({
        user_email: user.email
      });

      if (balances && balances.length > 0) {
        setBalance(balances[0]);
      }
    } catch (error) {
      console.error('Error loading user data:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleTransferComplete = () => {
    loadUserData();
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center p-12">
        <Loader2 className="w-8 h-8 text-purple-400 animate-spin" />
      </div>
    );
  }

  const isActive = membership?.status === 'active';
  const daysRemaining = membership?.end_date 
    ? Math.ceil((new Date(membership.end_date) - new Date()) / (1000 * 60 * 60 * 24))
    : 0;

  return (
    <Tabs defaultValue="overview" className="w-full">
      <TabsList className="grid w-full grid-cols-5 bg-slate-800/50">
        <TabsTrigger value="overview" className="data-[state=active]:bg-purple-600">
          <Wallet className="w-4 h-4 mr-2" />
          <span className="hidden md:inline">Resumen</span>
        </TabsTrigger>
        <TabsTrigger value="identities" className="data-[state=active]:bg-purple-600">
          <Link2 className="w-4 h-4 mr-2" />
          <span className="hidden md:inline">Identidades</span>
        </TabsTrigger>
        <TabsTrigger value="transfer" className="data-[state=active]:bg-cyan-600">
          <Send className="w-4 h-4 mr-2" />
          <span className="hidden md:inline">Transferir</span>
        </TabsTrigger>
        <TabsTrigger value="history" className="data-[state=active]:bg-purple-600">
          <History className="w-4 h-4 mr-2" />
          <span className="hidden md:inline">Historial</span>
        </TabsTrigger>
        <TabsTrigger value="verify" className="data-[state=active]:bg-cyan-600">
          <CheckCircle2 className="w-4 h-4 mr-2" />
          <span className="hidden md:inline">Verificar</span>
        </TabsTrigger>
      </TabsList>

      <TabsContent value="overview" className="space-y-6 mt-6">
        {/* Estado de Membres칤a */}
        <Card className={`${isActive ? 'bg-green-900/20 border-green-500/50' : 'bg-red-900/20 border-red-500/50'}`}>
          <CardContent className="p-6">
            <div className="flex items-center gap-4 mb-4">
              {isActive ? (
                <CheckCircle2 className="w-12 h-12 text-green-400" />
              ) : (
                <XCircle className="w-12 h-12 text-red-400" />
              )}
              <div>
                <h3 className="text-2xl font-bold text-white">
                  {isActive ? 'Membres칤a Activa' : 'Sin Membres칤a Activa'}
                </h3>
                {membership && (
                  <p className="text-slate-300">
                    Plan: <span className="font-semibold capitalize">{membership.plan_type}</span>
                  </p>
                )}
              </div>
            </div>

            {isActive && membership && (
              <div className="space-y-2">
                <div className="flex items-center gap-2 text-slate-300">
                  <Calendar className="w-4 h-4" />
                  <span>Expira: {format(new Date(membership.end_date), 'dd/MM/yyyy HH:mm')}</span>
                </div>
                <div className="flex items-center gap-2 text-slate-300">
                  <Crown className="w-4 h-4 text-yellow-400" />
                  <span>D칤as restantes: {daysRemaining}</span>
                </div>
              </div>
            )}
          </CardContent>
        </Card>

        {/* Saldo AMR */}
        {balance && (
          <Card className="bg-slate-900/50 border-cyan-500/30">
            <CardContent className="p-6">
              <h3 className="text-xl font-bold text-white mb-2">Saldo AMR</h3>
              <p className="text-4xl font-bold text-transparent bg-gradient-to-r from-cyan-400 to-blue-400 bg-clip-text">
                {balance.balance} AMR
              </p>
            </CardContent>
          </Card>
        )}

        {/* Generador de Pasaporte */}
        {isActive && (
          <Card className="bg-gradient-to-br from-purple-900/30 to-cyan-900/30 border-purple-500/50">
            <CardContent className="p-6">
              <div className="text-center space-y-4">
                <div className="inline-flex items-center justify-center w-16 h-16 rounded-full bg-purple-500/20 mb-2">
                  <Crown className="w-8 h-8 text-purple-400" />
                </div>
                <h3 className="text-xl font-bold text-white">
                  Pasaporte Ciudadano Arkaios
                </h3>
                <p className="text-slate-300 text-sm">
                  Descarga tu llave .arkaios cifrada con tu membres칤a activa.
                  칔sala para sincronizar tu cuenta con el Nexo de Vercel.
                </p>
                <ArkaiosKeyGenerator 
                  userData={balance || { email: user.email }} 
                  membershipData={membership}
                />
                <p className="text-xs text-slate-500">
                  Protocolo: AES-256 | Version: 2.5 | BITCOINLAND-BIFROST
                </p>
              </div>
            </CardContent>
          </Card>
        )}

        {!isActive && (
          <Card className="bg-yellow-900/20 border-yellow-500/50">
            <CardContent className="p-6 text-center">
              <p className="text-yellow-300">
                Compra una membres칤a para poder generar tu Pasaporte Ciudadano y acceder al Nexo completo.
              </p>
            </CardContent>
          </Card>
        )}
      </TabsContent>

      <TabsContent value="identities" className="mt-6">
        {balance ? (
          <IdentityLinker 
            user={user} 
            balance={balance}
            onUpdate={loadUserData}
          />
        ) : (
          <Card className="bg-yellow-900/20 border-yellow-500/50">
            <CardContent className="p-6 text-center">
              <p className="text-yellow-300">
                Necesitas una cuenta AMR para gestionar identidades vinculadas.
              </p>
            </CardContent>
          </Card>
        )}
      </TabsContent>

      <TabsContent value="transfer" className="mt-6">
        {balance ? (
          <TransferSystem 
            user={user} 
            currentBalance={balance.balance}
            onTransferComplete={handleTransferComplete}
          />
        ) : (
          <Card className="bg-yellow-900/20 border-yellow-500/50">
            <CardContent className="p-6 text-center">
              <p className="text-yellow-300">
                Necesitas una cuenta AMR para realizar transferencias.
              </p>
            </CardContent>
          </Card>
        )}
      </TabsContent>

      <TabsContent value="history" className="mt-6">
        <TransactionHistory user={user} />
      </TabsContent>

      <TabsContent value="verify" className="mt-6">
        <PassportVerifier />
      </TabsContent>
    </Tabs>
  );
}

