                Ver Planes de Membres칤a
              </Button>
            </CardContent>
          </Card>
        </motion.div>
      </div>
    );
  }

  return children;
}

components.arkaios/MembershipPurchase:
import React, { useState } from 'react';
import { base44 } from '@/api/base44Client';
import { Button } from '@/components/ui/button';
import { Card, CardContent } from '@/components/ui/card';
import { CheckCircle2, XCircle, Loader2, AlertTriangle } from 'lucide-react';

export default function MembershipPurchase({ user, plan, onSuccess, onCancel }) {
  const [isProcessing, setIsProcessing] = useState(false);
  const [message, setMessage] = useState(null);

  const calculateEndDate = (planType) => {
    const now = new Date();
    switch (planType) {
      case 'monthly':
        return new Date(now.setMonth(now.getMonth() + 1));
      case 'quarterly':
        return new Date(now.setMonth(now.getMonth() + 3));
      case 'biannual':
        return new Date(now.setMonth(now.getMonth() + 6));
      case 'annual':
        return new Date(now.setFullYear(now.getFullYear() + 1));
      default:
        return new Date(now.setMonth(now.getMonth() + 1));
    }
  };

  const handlePurchase = async () => {
    setIsProcessing(true);
    setMessage(null);

    try {
      // 1. Verificar balance del usuario
      const balances = await base44.entities.AmrBalance.filter({
        user_email: user.email
      });

      if (!balances || balances.length === 0) {
        setMessage({ 
          type: 'error', 
          text: 'No tienes una cuenta AMR. Por favor, canjea un c칩digo primero.' 
        });
        setIsProcessing(false);
        return;
      }

      const userBalance = balances[0];

      // 2. Validar saldo suficiente
      if (userBalance.balance < plan.price) {
        setMessage({ 
          type: 'error', 
          text: `Saldo insuficiente. Tienes ${userBalance.balance} AMR, necesitas ${plan.price} AMR.` 
        });
        setIsProcessing(false);
        return;
      }

      // 3. Verificar si ya tiene membres칤a activa
      const existingMemberships = await base44.entities.Membership.filter({
        user_email: user.email,
        status: 'active'
      });

      if (existingMemberships && existingMemberships.length > 0) {
        const activeMembership = existingMemberships[0];
        const endDate = new Date(activeMembership.end_date);
        const now = new Date();

        if (endDate > now) {
          setMessage({ 
            type: 'error', 
            text: 'Ya tienes una membres칤a activa. Espera a que expire para comprar otra.' 
          });
          setIsProcessing(false);
          return;
        }
      }

      // 4. Deducir del balance
      const newBalance = userBalance.balance - plan.price;
      await base44.entities.AmrBalance.update(userBalance.id, {
        balance: newBalance
      });

      // 5. Crear registro de transacci칩n
      const transaction = await base44.entities.Transaction.create({
        user_email: user.email,
        type: 'membership_payment',
        amount: -plan.price,
        description: `Compra de membres칤a ${plan.name}`,
        status: 'completed',
        reference: plan.id
      });

      // 6. Crear o actualizar membres칤a
      const startDate = new Date();
      const endDate = calculateEndDate(plan.id);

      if (existingMemberships && existingMemberships.length > 0) {
        // Actualizar membres칤a existente
        await base44.entities.Membership.update(existingMemberships[0].id, {
          plan_type: plan.id,
          start_date: startDate.toISOString(),
          end_date: endDate.toISOString(),
          status: 'active',
          amount_paid: plan.price,
          transaction_id: transaction.id
        });
      } else {
        // Crear nueva membres칤a
        await base44.entities.Membership.create({
          user_email: user.email,
          plan_type: plan.id,
          start_date: startDate.toISOString(),
          end_date: endDate.toISOString(),
          status: 'active',
          amount_paid: plan.price,
          transaction_id: transaction.id
        });
      }

      setMessage({ 
        type: 'success', 
        text: `춰Membres칤a ${plan.name} activada exitosamente! Ya puedes descargar tu Pasaporte Ciudadano.` 
      });

      // Esperar un momento para que el usuario vea el mensaje
      setTimeout(() => {
        if (onSuccess) onSuccess();
      }, 2000);

    } catch (error) {
      console.error('Error al procesar la compra:', error);
      setMessage({ 
        type: 'error', 
        text: 'Error al procesar la compra. Por favor intenta de nuevo.' 
      });
    } finally {
      setIsProcessing(false);
    }
  };

  return (
    <Card className="bg-slate-900/50 border-cyan-500/30">
      <CardContent className="p-6">
        <div className="text-center space-y-4">
          <h3 className="text-2xl font-bold text-white">
            Confirmar Compra
          </h3>
          <div className="bg-slate-800/50 rounded-lg p-6">
            <p className="text-slate-300 mb-2">Plan seleccionado:</p>
            <p className="text-3xl font-bold text-transparent bg-gradient-to-r from-purple-400 to-cyan-400 bg-clip-text mb-1">
              {plan.name}
            </p>
            <p className="text-slate-400 text-sm mb-4">{plan.description}</p>
            <div className="flex items-center justify-center gap-2 text-2xl font-bold text-yellow-400">
              <span>{plan.price}</span>
              <span className="text-lg text-slate-400">AMR</span>
            </div>
          </div>

          {message && (
            <div className={`flex items-center gap-2 p-4 rounded-lg ${
              message.type === 'success' 
                ? 'bg-green-900/30 border border-green-500/50' 
                : 'bg-red-900/30 border border-red-500/50'
            }`}>
              {message.type === 'success' ? (
                <CheckCircle2 className="w-6 h-6 text-green-400 flex-shrink-0" />
              ) : (
                <XCircle className="w-6 h-6 text-red-400 flex-shrink-0" />
              )}
              <p className={`text-sm ${message.type === 'success' ? 'text-green-300' : 'text-red-300'}`}>
                {message.text}
              </p>
            </div>
          )}

          <div className="flex gap-3">
            <Button
              onClick={onCancel}
              variant="outline"
              className="flex-1 border-slate-600 text-slate-300"
              disabled={isProcessing}
            >
              Cancelar
            </Button>
            <Button
              onClick={handlePurchase}
              className="flex-1 bg-gradient-to-r from-purple-600 to-cyan-600 hover:from-purple-500 hover:to-cyan-500"
              disabled={isProcessing}
            >
              {isProcessing ? (
                <>
                  <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                  Procesando...
                </>
              ) : (
                <>
                  <CheckCircle2 className="w-4 h-4 mr-2" />
                  Confirmar Compra
                </>
              )}
            </Button>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

components.arkaios/PassportVerifier:
import React, { useState } from 'react';
import { base44 } from '@/api/base44Client';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Shield, CheckCircle2, XCircle, Loader2, Search } from 'lucide-react';

export default function PassportVerifier() {
  const [searchEmail, setSearchEmail] = useState('');
  const [isVerifying, setIsVerifying] = useState(false);
  const [verificationResult, setVerificationResult] = useState(null);

  const handleVerify = async () => {
    if (!searchEmail.trim()) return;

    setIsVerifying(true);
    setVerificationResult(null);

    try {
      // Buscar el balance del usuario por email
      const balances = await base44.entities.AmrBalance.filter({
        user_email: searchEmail.trim()
      });

      if (balances && balances.length > 0) {
        const balance = balances[0];

        // Verificar si tiene membres칤a activa
        const memberships = await base44.entities.Membership.filter({
          user_email: searchEmail.trim(),
          status: 'active'
        });

        const hasMembership = memberships && memberships.length > 0;
        const membership = hasMembership ? memberships[0] : null;

        // Verificar si no est치 expirada
        let isActive = false;
        if (membership) {
          const endDate = new Date(membership.end_date);
          isActive = endDate > new Date();
        }

        setVerificationResult({
          found: true,
          email: searchEmail.trim(),
          passport_id: balance.wallet_address || `ARK-${searchEmail.split('@')[0].toUpperCase()}`,
          membership_status: isActive ? 'active' : (hasMembership ? 'expired' : 'none'),
          membership_plan: membership?.plan_type || null,
          membership_expiry: membership?.end_date || null,
          balance: balance.balance,
          linked_ids: balance.linked_ids || []
        });
      } else {
        setVerificationResult({
          found: false,
          email: searchEmail.trim()
        });
      }
    } catch (error) {
      console.error('Error verifying passport:', error);
      setVerificationResult({
        found: false,
        email: searchEmail.trim(),
        error: 'Error al verificar'
      });
    } finally {
      setIsVerifying(false);
    }
  };

  const checkLinkedId = (email) => {
    if (!verificationResult?.linked_ids) return false;
    return verificationResult.linked_ids.some(
      linked => linked.type === 'google_email' && linked.value === email && linked.verified
    );
  };

  return (
    <Card className="bg-slate-900/50 border-cyan-500/30">
      <CardContent className="p-6">
        <div className="flex items-center gap-3 mb-4">
          <Shield className="w-6 h-6 text-cyan-400" />
          <h3 className="text-xl font-bold text-white">Verificador de Pasaporte</h3>
        </div>

        <p className="text-slate-400 text-sm mb-6">
          Verifica si un email tiene un Pasaporte Ciudadano ARKAIOS registrado
        </p>

        <div className="space-y-4">
          <div className="flex gap-2">
            <Input
              placeholder="Ingresa email a verificar"
              value={searchEmail}
              onChange={(e) => setSearchEmail(e.target.value)}
              className="bg-slate-800 border-slate-700 text-white"
              onKeyPress={(e) => e.key === 'Enter' && handleVerify()}
            />
            <Button
              onClick={handleVerify}
              disabled={isVerifying || !searchEmail.trim()}
              className="bg-gradient-to-r from-cyan-600 to-blue-600 hover:from-cyan-500 hover:to-blue-500"
            >
              {isVerifying ? (
                <Loader2 className="w-4 h-4 animate-spin" />
              ) : (
                <Search className="w-4 h-4" />
              )}
            </Button>
          </div>

          {verificationResult && (
            <div className={`p-4 rounded-lg border ${
              verificationResult.found 
                ? 'bg-green-900/20 border-green-500/50' 
                : 'bg-red-900/20 border-red-500/50'
            }`}>
              <div className="flex items-start gap-3">
                {verificationResult.found ? (
                  <CheckCircle2 className="w-6 h-6 text-green-400 flex-shrink-0 mt-1" />
                ) : (
                  <XCircle className="w-6 h-6 text-red-400 flex-shrink-0 mt-1" />
                )}
                <div className="flex-1">
                  <p className={`font-bold mb-2 ${
                    verificationResult.found ? 'text-green-300' : 'text-red-300'
                  }`}>
                    {verificationResult.found 
                      ? 'Pasaporte Encontrado' 
                      : 'Pasaporte No Encontrado'}
                  </p>

                  {verificationResult.found && (
                    <div className="space-y-2 text-sm">
                      <div className="flex justify-between">
                        <span className="text-slate-400">Email:</span>
                        <span className="text-white font-mono">{verificationResult.email}</span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-slate-400">Passport ID:</span>
                        <span className="text-cyan-300 font-mono">{verificationResult.passport_id}</span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-slate-400">Membres칤a:</span>
                        <span className={`font-semibold ${
                          verificationResult.membership_status === 'active' 
                            ? 'text-green-400' 
                            : 'text-red-400'
                        }`}>
                          {verificationResult.membership_status === 'active' ? 'Activa' : 
                           verificationResult.membership_status === 'expired' ? 'Expirada' : 'Ninguna'}
                        </span>
                      </div>
                      {verificationResult.membership_plan && (
                        <div className="flex justify-between">
                          <span className="text-slate-400">Plan:</span>
                          <span className="text-white capitalize">{verificationResult.membership_plan}</span>
                        </div>
                      )}
                      <div className="flex justify-between">
                        <span className="text-slate-400">Balance AMR:</span>
                        <span className="text-yellow-400 font-bold">{verificationResult.balance} AMR</span>
                      </div>
                      {verificationResult.linked_ids.length > 0 && (
                        <div className="mt-3 pt-3 border-t border-slate-700">
                          <p className="text-slate-400 mb-2 text-xs">Identidades Vinculadas:</p>
                          <div className="space-y-1">
                            {verificationResult.linked_ids.map((linked, index) => (
                              <div key={index} className="flex items-center gap-2 text-xs">
                                <CheckCircle2 className="w-3 h-3 text-green-400" />
                                <span className="text-slate-300">{linked.type}: </span>
                                <span className="text-white font-mono">{linked.value}</span>
                              </div>
                            ))}
                          </div>
                        </div>
                      )}
                    </div>
                  )}

                  {!verificationResult.found && (
                    <p className="text-slate-400 text-sm">
                      El email {verificationResult.email} no tiene un Pasaporte Ciudadano registrado en ARKAIOS.
                    </p>
                  )}
                </div>
              </div>
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

