components.arkaios/RedeemCodeSystem:
import React, { useState } from 'react';
import { base44 } from '@/api/base44Client';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Gift, Loader2, CheckCircle2, AlertCircle } from 'lucide-react';

export default function RedeemCodeSystem({ user, onSuccess }) {
  const [code, setCode] = useState('');
  const [isRedeeming, setIsRedeeming] = useState(false);
  const [message, setMessage] = useState(null);

  const handleRedeem = async () => {
    if (!code || code.length !== 16) {
      setMessage({ type: 'error', text: 'El c칩digo debe tener 16 caracteres' });
      return;
    }

    setIsRedeeming(true);
    setMessage(null);

    try {
      // Buscar el c칩digo
      const codes = await base44.entities.RedeemCode.filter({
        code: code.toUpperCase(),
        status: 'available'
      });

      if (!codes || codes.length === 0) {
        setMessage({ type: 'error', text: 'C칩digo inv치lido o ya canjeado' });
        setIsRedeeming(false);
        return;
      }

      const redeemCode = codes[0];

      // Verificar expiraci칩n
      if (redeemCode.expires_at) {
        const expiryDate = new Date(redeemCode.expires_at);
        if (expiryDate < new Date()) {
          setMessage({ type: 'error', text: 'Este c칩digo ha expirado' });
          setIsRedeeming(false);
          return;
        }
      }

      // Actualizar c칩digo a canjeado
      await base44.entities.RedeemCode.update(redeemCode.id, {
        status: 'redeemed',
        redeemed_by: user.email,
        redeemed_at: new Date().toISOString()
      });

      // Obtener o crear balance del usuario
      let balances = await base44.entities.AmrBalance.filter({
        user_email: user.email
      });

      let userBalance;
      if (!balances || balances.length === 0) {
        userBalance = await base44.entities.AmrBalance.create({
          user_email: user.email,
          balance: redeemCode.amount
        });
      } else {
        userBalance = balances[0];
        await base44.entities.AmrBalance.update(userBalance.id, {
          balance: userBalance.balance + redeemCode.amount
        });
      }

      // Registrar transacci칩n
      await base44.entities.Transaction.create({
        user_email: user.email,
        type: 'code_redeem',
        amount: redeemCode.amount,
        description: `C칩digo canjeado: ${code}`,
        reference: redeemCode.id
      });

      setMessage({ 
        type: 'success', 
        text: `춰${redeemCode.amount} AMR a침adidos a tu cuenta!` 
      });
      setCode('');

      if (onSuccess) {
        setTimeout(() => onSuccess(), 2000);
      }
    } catch (error) {
      console.error('Error redeeming code:', error);
      setMessage({ type: 'error', text: 'Error al canjear el c칩digo' });
    } finally {
      setIsRedeeming(false);
    }
  };

  return (
    <Card className="bg-slate-900/50 border-cyan-500/30">
      <CardContent className="p-6">
        <div className="flex items-center gap-3 mb-4">
          <Gift className="w-6 h-6 text-cyan-400" />
          <h3 className="text-xl font-bold text-white">Canjear C칩digo AMR</h3>
        </div>

        <div className="space-y-4">
          <div>
            <Input
              placeholder="Ingresa c칩digo de 16 caracteres"
              value={code}
              onChange={(e) => setCode(e.target.value.toUpperCase())}
              maxLength={16}
              className="bg-slate-800 border-slate-700 text-white"
            />
          </div>

          {message && (
            <div className={`flex items-center gap-2 p-3 rounded-lg ${
              message.type === 'success' 
                ? 'bg-green-500/20 border border-green-500/50 text-green-300'
                : 'bg-red-500/20 border border-red-500/50 text-red-300'
            }`}>
              {message.type === 'success' ? (
                <CheckCircle2 className="w-5 h-5" />
              ) : (
                <AlertCircle className="w-5 h-5" />
              )}
              <span>{message.text}</span>
            </div>
          )}

          <Button
            onClick={handleRedeem}
            disabled={isRedeeming || code.length !== 16}
            className="w-full bg-gradient-to-r from-cyan-600 to-blue-600 hover:from-cyan-500 hover:to-blue-500"
          >
            {isRedeeming ? (
              <>
                <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                Canjeando...
              </>
            ) : (
              <>
                <Gift className="w-4 h-4 mr-2" />
                Canjear C칩digo
              </>
            )}
          </Button>
        </div>
      </CardContent>
    </Card>
  );
}

