components.arkaios/TransactionHistory:
import React, { useState, useEffect } from 'react';
import { base44 } from '@/api/base44Client';
import { Card, CardContent } from '@/components/ui/card';
import { ScrollArea } from '@/components/ui/scroll-area';
import { History, ArrowUpRight, ArrowDownLeft, Gift, CreditCard, Loader2 } from 'lucide-react';
import { format } from 'date-fns';

export default function TransactionHistory({ user }) {
  const [transactions, setTransactions] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    loadTransactions();
  }, [user]);

  const loadTransactions = async () => {
    setIsLoading(true);
    try {
      const userTransactions = await base44.entities.Transaction.filter({
        user_email: user.email
      }, '-created_date', 50);

      setTransactions(userTransactions || []);
    } catch (error) {
      console.error('Error loading transactions:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const getTransactionIcon = (type) => {
    switch (type) {
      case 'transfer_sent':
        return <ArrowUpRight className="w-5 h-5 text-red-400" />;
      case 'transfer_received':
        return <ArrowDownLeft className="w-5 h-5 text-green-400" />;
      case 'code_redeem':
        return <Gift className="w-5 h-5 text-purple-400" />;
      case 'membership_payment':
        return <CreditCard className="w-5 h-5 text-yellow-400" />;
      default:
        return <History className="w-5 h-5 text-cyan-400" />;
    }
  };

  const getTransactionColor = (type) => {
    switch (type) {
      case 'transfer_sent':
      case 'membership_payment':
        return 'text-red-400';
      case 'transfer_received':
      case 'code_redeem':
        return 'text-green-400';
      default:
        return 'text-slate-400';
    }
  };

  if (isLoading) {
    return (
      <Card className="bg-slate-900/50 border-purple-500/30">
        <CardContent className="p-6">
          <div className="flex items-center justify-center py-8">
            <Loader2 className="w-8 h-8 text-purple-400 animate-spin" />
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className="bg-slate-900/50 border-purple-500/30">
      <CardContent className="p-6">
        <div className="flex items-center gap-3 mb-4">
          <History className="w-6 h-6 text-purple-400" />
          <h3 className="text-xl font-bold text-white">Historial de Transacciones</h3>
        </div>

        {transactions.length === 0 ? (
          <div className="text-center py-8">
            <p className="text-slate-400">No hay transacciones registradas</p>
          </div>
        ) : (
          <ScrollArea className="h-[400px] pr-4">
            <div className="space-y-3">
              {transactions.map((transaction) => (
                <div
                  key={transaction.id}
                  className="bg-slate-800/50 rounded-lg p-4 border border-slate-700/50 hover:border-purple-500/50 transition-colors"
                >
                  <div className="flex items-start justify-between">
                    <div className="flex items-start gap-3">
                      <div className="mt-1">
                        {getTransactionIcon(transaction.type)}
                      </div>
                      <div>
                        <p className="text-white font-medium">
                          {transaction.description || transaction.type}
                        </p>
                        <p className="text-xs text-slate-500 mt-1">
                          {format(new Date(transaction.created_date), 'dd/MM/yyyy HH:mm:ss')}
                        </p>
                        {transaction.recipient_email && (
                          <p className="text-xs text-slate-400 mt-1">
                            Para: {transaction.recipient_email}
                          </p>
                        )}
                        {transaction.reference && (
                          <p className="text-xs text-slate-400 mt-1">
                            De: {transaction.reference}
                          </p>
                        )}
                      </div>
                    </div>
                    <div className="text-right">
                      <p className={`text-lg font-bold ${getTransactionColor(transaction.type)}`}>
                        {transaction.amount > 0 ? '+' : ''}{transaction.amount} AMR
                      </p>
                      <span className={`text-xs px-2 py-1 rounded-full ${
                        transaction.status === 'completed' 
                          ? 'bg-green-900/30 text-green-400' 
                          : transaction.status === 'pending'
                          ? 'bg-yellow-900/30 text-yellow-400'
                          : 'bg-red-900/30 text-red-400'
                      }`}>
                        {transaction.status}
                      </span>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </ScrollArea>
        )}
      </CardContent>
    </Card>
  );
}

components.arkaios/TransferSystem:
import React, { useState } from 'react';
import { base44 } from '@/api/base44Client';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Send, Loader2, CheckCircle2, XCircle } from 'lucide-react';

export default function TransferSystem({ user, currentBalance, onTransferComplete }) {
  const [recipientEmail, setRecipientEmail] = useState('');
  const [amount, setAmount] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);
  const [message, setMessage] = useState(null);

  const handleTransfer = async (e) => {
    e.preventDefault();
    setMessage(null);

    // Validaciones b치sicas
    if (!recipientEmail || !amount) {
      setMessage({ type: 'error', text: 'Por favor completa todos los campos' });
      return;
    }

    const transferAmount = parseFloat(amount);
    if (isNaN(transferAmount) || transferAmount <= 0) {
      setMessage({ type: 'error', text: 'Monto inv치lido' });
      return;
    }

    if (transferAmount > currentBalance) {
      setMessage({ type: 'error', text: 'Saldo insuficiente' });
      return;
    }

    if (recipientEmail === user.email) {
      setMessage({ type: 'error', text: 'No puedes transferirte a ti mismo' });
      return;
    }

    setIsProcessing(true);

    try {
      // Verificar que el destinatario existe
      const recipientBalances = await base44.entities.AmrBalance.filter({
        user_email: recipientEmail
      });

      if (!recipientBalances || recipientBalances.length === 0) {
        setMessage({ type: 'error', text: 'El destinatario no tiene una cuenta AMR' });
        setIsProcessing(false);
        return;
      }

      const recipientBalance = recipientBalances[0];

      // Obtener balance del emisor
      const senderBalances = await base44.entities.AmrBalance.filter({
        user_email: user.email
      });

      if (!senderBalances || senderBalances.length === 0) {
        setMessage({ type: 'error', text: 'No tienes una cuenta AMR' });
        setIsProcessing(false);
        return;
      }

      const senderBalance = senderBalances[0];

      // Verificaci칩n final de saldo
      if (senderBalance.balance < transferAmount) {
        setMessage({ type: 'error', text: 'Saldo insuficiente' });
        setIsProcessing(false);
        return;
      }

      // Crear transacci칩n de env칤o
      await base44.entities.Transaction.create({
        user_email: user.email,
        type: 'transfer_sent',
        amount: -transferAmount,
        description: `Transferencia enviada a ${recipientEmail}`,
        status: 'completed',
        recipient_email: recipientEmail
      });

      // Crear transacci칩n de recepci칩n
      await base44.entities.Transaction.create({
        user_email: recipientEmail,
        type: 'transfer_received',
        amount: transferAmount,
        description: `Transferencia recibida de ${user.email}`,
        status: 'completed',
        reference: user.email
      });

      // Actualizar balance del emisor
      await base44.entities.AmrBalance.update(senderBalance.id, {
        balance: senderBalance.balance - transferAmount
      });

      // Actualizar balance del receptor
      await base44.entities.AmrBalance.update(recipientBalance.id, {
        balance: recipientBalance.balance + transferAmount
      });

      setMessage({ 
        type: 'success', 
        text: `Transferencia de ${transferAmount} AMR enviada exitosamente` 
      });
      setRecipientEmail('');
      setAmount('');

      // Notificar al componente padre
      if (onTransferComplete) {
        onTransferComplete();
      }

    } catch (error) {
      console.error('Error en transferencia:', error);
      setMessage({ type: 'error', text: 'Error al procesar la transferencia' });
    } finally {
      setIsProcessing(false);
    }
  };

  return (
    <Card className="bg-slate-900/50 border-cyan-500/30">
      <CardContent className="p-6">
        <div className="flex items-center gap-3 mb-4">
          <Send className="w-6 h-6 text-cyan-400" />
          <h3 className="text-xl font-bold text-white">Transferencias P2P</h3>
        </div>

        <form onSubmit={handleTransfer} className="space-y-4">
          <div>
            <Label className="text-slate-300">Email del destinatario</Label>
            <Input
              type="email"
              value={recipientEmail}
              onChange={(e) => setRecipientEmail(e.target.value)}
              placeholder="usuario@ejemplo.com"
              className="bg-slate-800/50 border-cyan-500/30 text-white mt-1"
              disabled={isProcessing}
            />
          </div>

          <div>
            <Label className="text-slate-300">Monto (AMR)</Label>
            <Input
              type="number"
              step="0.01"
              value={amount}
              onChange={(e) => setAmount(e.target.value)}
              placeholder="0.00"
              className="bg-slate-800/50 border-cyan-500/30 text-white mt-1"
              disabled={isProcessing}
            />
            <p className="text-xs text-slate-500 mt-1">
              Saldo disponible: {currentBalance} AMR
            </p>
          </div>

          <Button
            type="submit"
            disabled={isProcessing}
            className="w-full bg-gradient-to-r from-cyan-600 to-blue-600 hover:from-cyan-500 hover:to-blue-500"
          >
            {isProcessing ? (
              <>
                <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                Procesando...
              </>
            ) : (
              <>
                <Send className="w-4 h-4 mr-2" />
                Enviar Transferencia
              </>
            )}
          </Button>

          {message && (
            <div className={`flex items-center gap-2 p-3 rounded-lg ${
              message.type === 'success' 
                ? 'bg-green-900/30 border border-green-500/50' 
                : 'bg-red-900/30 border border-red-500/50'
            }`}>
              {message.type === 'success' ? (
                <CheckCircle2 className="w-5 h-5 text-green-400" />
              ) : (
                <XCircle className="w-5 h-5 text-red-400" />
              )}
              <p className={message.type === 'success' ? 'text-green-300' : 'text-red-300'}>
                {message.text}
              </p>
            </div>
          )}
        </form>
      </CardContent>
    </Card>
  );
}

components.arkaios/CajeroAmr:
import React, { useState, useEffect } from 'react';
import { base44 } from '@/api/base44Client';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Banknote, Send, ArrowDownUp, Loader2, AlertCircle, CheckCircle2, TrendingUp, Landmark } from 'lucide-react';

export default function CajeroAmr({ user, onClose }) {
  const [balance, setBalance] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [transferAmount, setTransferAmount] = useState('');
  const [recipientEmail, setRecipientEmail] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);
  const [message, setMessage] = useState(null);
  const [withdrawAmount, setWithdrawAmount] = useState('');
  const [transactions, setTransactions] = useState([]);

  useEffect(() => {
    loadUserData();
  }, [user]);

  const loadUserData = async () => {
    setIsLoading(true);
    try {
      const balances = await base44.entities.AmrBalance.filter({
        user_email: user.email
      });

      if (balances && balances.length > 0) {
        setBalance(balances[0]);
      }

      // Cargar 칰ltimas transacciones
      const userTransactions = await base44.entities.Transaction.filter({
        user_email: user.email
      }, '-created_date', 5);

      setTransactions(userTransactions || []);
    } catch (error) {
      console.error('Error loading data:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleTransfer = async () => {
    if (!recipientEmail || !transferAmount || parseFloat(transferAmount) <= 0) {
      setMessage({ type: 'error', text: 'Ingresa un destinatario y monto v치lidos' });
      return;
    }

    if (parseFloat(transferAmount) > balance.balance) {
      setMessage({ type: 'error', text: 'Saldo insuficiente' });
      return;
    }

    if (recipientEmail.toLowerCase() === user.email.toLowerCase()) {
      setMessage({ type: 'error', text: 'No puedes transferirte a ti mismo' });
      return;
    }

    setIsProcessing(true);
    setMessage(null);

    try {
      // Verificar que el destinatario existe
      const recipientBalances = await base44.entities.AmrBalance.filter({
        user_email: recipientEmail
      });

      if (!recipientBalances || recipientBalances.length === 0) {
        setMessage({ type: 'error', text: 'El destinatario no tiene cuenta AMR' });
        setIsProcessing(false);
        return;
      }

      const amount = parseFloat(transferAmount);
      const recipientBalance = recipientBalances[0];

      // Descontar del emisor
      await base44.entities.AmrBalance.update(balance.id, {
        balance: balance.balance - amount
      });

      // Agregar al receptor
      await base44.entities.AmrBalance.update(recipientBalance.id, {
        balance: recipientBalance.balance + amount
      });

      // Crear transacciones
      await base44.entities.Transaction.create({
        user_email: user.email,
        type: 'transfer_sent',
        amount: -amount,
        description: `Transferencia enviada a ${recipientEmail}`,
        recipient_email: recipientEmail
      });

      await base44.entities.Transaction.create({
        user_email: recipientEmail,
        type: 'transfer_received',
        amount: amount,
        description: `Transferencia recibida de ${user.email}`,
        reference: user.email
      });

      setMessage({ type: 'success', text: `춰${amount} AMR transferidos exitosamente!` });
      setTransferAmount('');
      setRecipientEmail('');

      setTimeout(() => {
        loadUserData();
      }, 1500);
    } catch (error) {
      console.error('Error in transfer:', error);
      setMessage({ type: 'error', text: 'Error al procesar la transferencia' });
    } finally {
      setIsProcessing(false);
    }
  };

  const handleWithdraw = async () => {
    if (!withdrawAmount || parseFloat(withdrawAmount) <= 0) {
      setMessage({ type: 'error', text: 'Ingresa un monto v치lido' });
      return;
    }

    if (parseFloat(withdrawAmount) > balance.balance) {
      setMessage({ type: 'error', text: 'Saldo insuficiente' });
      return;
    }

    setIsProcessing(true);
    setMessage(null);

    try {
      const amount = parseFloat(withdrawAmount);

      await base44.entities.AmrBalance.update(balance.id, {
        balance: balance.balance - amount
      });

      await base44.entities.Transaction.create({
        user_email: user.email,
        type: 'purchase',
        amount: -amount,
        description: `Retiro institucional de ${amount} AMR`,
        status: 'completed'
      });

      setMessage({ type: 'success', text: `Retiro de ${amount} AMR procesado` });
      setWithdrawAmount('');

      setTimeout(() => {
        loadUserData();
      }, 1500);
    } catch (error) {
      console.error('Error in withdrawal:', error);
      setMessage({ type: 'error', text: 'Error al procesar el retiro' });
    } finally {
      setIsProcessing(false);
    }
  };

  const isInstitutional = balance?.account_level === 'ESCUELA' || balance?.account_level === 'INSTITUCI칍N';

  if (isLoading) {
    return (
      <div className="flex items-center justify-center p-12">
        <Loader2 className="w-8 h-8 text-purple-400 animate-spin" />
      </div>
    );
  }

  if (!balance) {
    return (
      <Card className="bg-red-900/20 border-red-500/50">
        <CardContent className="p-6 text-center">
          <AlertCircle className="w-12 h-12 text-red-400 mx-auto mb-3" />
          <p className="text-red-300">No tienes una cuenta AMR activa</p>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header - Saldo Principal */}
      <Card className="bg-gradient-to-br from-purple-900/40 via-slate-900/40 to-black/60 border-purple-500/50 overflow-hidden relative">
        <div className="absolute inset-0 bg-[radial-gradient(circle_at_50%_120%,rgba(120,119,198,0.3),rgba(0,0,0,0))]"></div>
        <CardContent className="p-8 relative z-10">
          <div className="flex items-center justify-between mb-6">
            <div className="flex items-center gap-3">
              <div className="w-12 h-12 rounded-full bg-purple-500/20 flex items-center justify-center">
                <Landmark className="w-6 h-6 text-purple-400" />
              </div>
              <div>
                <p className="text-purple-300 text-sm uppercase tracking-wider">Cajero AMR Soberano</p>
                <p className="text-white/60 text-xs">{user.email}</p>
              </div>
            </div>
            {isInstitutional && (
              <div className="px-3 py-1 rounded-full bg-yellow-500/20 border border-yellow-500/50">
                <span className="text-yellow-300 text-xs font-bold">INSTITUCI칍N</span>
              </div>
            )}
          </div>

          <div className="space-y-2">
            <p className="text-purple-300 text-sm">Saldo Disponible</p>
            <p className="text-5xl font-bold text-transparent bg-gradient-to-r from-purple-400 via-pink-400 to-cyan-400 bg-clip-text">
              {balance.balance.toFixed(2)} AMR
            </p>
          </div>
        </CardContent>
      </Card>

      {/* Operaciones */}
      <Tabs defaultValue="transfer" className="w-full">
        <TabsList className="grid w-full grid-cols-3 bg-slate-900/50">
          <TabsTrigger value="transfer" className="data-[state=active]:bg-purple-600">
            <Send className="w-4 h-4 mr-2" />
            Transferir
          </TabsTrigger>
          <TabsTrigger value="history" className="data-[state=active]:bg-purple-600">
            <ArrowDownUp className="w-4 h-4 mr-2" />
            Historial
          </TabsTrigger>
          {isInstitutional && (
            <TabsTrigger value="withdraw" className="data-[state=active]:bg-yellow-600">
              <TrendingUp className="w-4 h-4 mr-2" />
              Retiro
            </TabsTrigger>
          )}
        </TabsList>

        {/* Transferir */}
        <TabsContent value="transfer" className="mt-6">
          <Card className="bg-slate-900/50 border-purple-500/30">
            <CardContent className="p-6 space-y-4">
              <div>
                <label className="text-purple-300 text-sm mb-2 block">Email del Destinatario</label>
                <Input
                  placeholder="usuario@ejemplo.com"
                  value={recipientEmail}
                  onChange={(e) => setRecipientEmail(e.target.value)}
                  className="bg-slate-800 border-slate-700 text-white"
                />
              </div>

              <div>
                <label className="text-purple-300 text-sm mb-2 block">Monto a Transferir</label>
                <Input
                  type="number"
                  placeholder="0.00"
                  value={transferAmount}
                  onChange={(e) => setTransferAmount(e.target.value)}
                  className="bg-slate-800 border-slate-700 text-white"
                />
              </div>

              {message && (
                <div className={`flex items-center gap-2 p-3 rounded-lg ${
                  message.type === 'success' 
                    ? 'bg-green-500/20 border border-green-500/50 text-green-300'
                    : 'bg-red-500/20 border border-red-500/50 text-red-300'
                }`}>
                  {message.type === 'success' ? (
                    <CheckCircle2 className="w-5 h-5" />
                  ) : (
                    <AlertCircle className="w-5 h-5" />
                  )}
                  <span>{message.text}</span>
                </div>
              )}

              <Button
                onClick={handleTransfer}
                disabled={isProcessing}
                className="w-full bg-gradient-to-r from-purple-600 to-pink-600 hover:from-purple-500 hover:to-pink-500"
              >
                {isProcessing ? (
                  <>
                    <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                    Procesando...
                  </>
                ) : (
                  <>
                    <Send className="w-4 h-4 mr-2" />
                    Enviar AMR
                  </>
                )}
              </Button>
            </CardContent>
          </Card>
        </TabsContent>

        {/* Historial */}
        <TabsContent value="history" className="mt-6">
          <Card className="bg-slate-900/50 border-purple-500/30">
            <CardContent className="p-6">
              <h3 className="text-white font-bold mb-4">칔ltimas Transacciones</h3>
              <div className="space-y-3">
                {transactions.length === 0 ? (
                  <p className="text-slate-400 text-center py-8">No hay transacciones recientes</p>
                ) : (
                  transactions.map((tx) => (
                    <div key={tx.id} className="flex items-center justify-between p-3 bg-slate-800/50 rounded-lg">
                      <div>
                        <p className="text-white text-sm">{tx.description}</p>
                        <p className="text-slate-400 text-xs">{new Date(tx.created_date).toLocaleString()}</p>
                      </div>
                      <p className={`font-bold ${tx.amount > 0 ? 'text-green-400' : 'text-red-400'}`}>
                        {tx.amount > 0 ? '+' : ''}{tx.amount} AMR
                      </p>
                    </div>
                  ))
                )}
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        {/* Retiro Institucional */}
        {isInstitutional && (
          <TabsContent value="withdraw" className="mt-6">
            <Card className="bg-yellow-900/20 border-yellow-500/50">
              <CardContent className="p-6 space-y-4">
                <div className="flex items-center gap-2 text-yellow-300 mb-4">
                  <Landmark className="w-5 h-5" />
                  <p className="font-bold">Panel Institucional - Retiro de Fondos</p>
                </div>

                <div>
                  <label className="text-yellow-300 text-sm mb-2 block">Monto a Retirar</label>
                  <Input
                    type="number"
                    placeholder="0.00"
                    value={withdrawAmount}
                    onChange={(e) => setWithdrawAmount(e.target.value)}
                    className="bg-slate-800 border-yellow-700 text-white"
                  />
                </div>

                {message && (
                  <div className={`flex items-center gap-2 p-3 rounded-lg ${
                    message.type === 'success' 
                      ? 'bg-green-500/20 border border-green-500/50 text-green-300'
                      : 'bg-red-500/20 border border-red-500/50 text-red-300'
                  }`}>
                    {message.type === 'success' ? (
                      <CheckCircle2 className="w-5 h-5" />
                    ) : (
                      <AlertCircle className="w-5 h-5" />
                    )}
                    <span>{message.text}</span>
                  </div>
                )}

                <Button
                  onClick={handleWithdraw}
                  disabled={isProcessing}
                  className="w-full bg-gradient-to-r from-yellow-600 to-orange-600 hover:from-yellow-500 hover:to-orange-500"
                >
                  {isProcessing ? (
                    <>
                      <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                      Procesando...
                    </>
                  ) : (
                    <>
                      <TrendingUp className="w-4 h-4 mr-2" />
