components.arkaios/MercadoPremium:
import React, { useState, useEffect } from 'react';
import { base44 } from '@/api/base44Client';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { ShoppingBag, CreditCard, Coins, Package, Sparkles, Loader2, AlertCircle, CheckCircle2 } from 'lucide-react';
import MembershipPurchase from './MembershipPurchase';

export default function MercadoPremium({ user, onClose }) {
  const [balance, setBalance] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [message, setMessage] = useState(null);

  useEffect(() => {
    loadBalance();
  }, [user]);

  const loadBalance = async () => {
    setIsLoading(true);
    try {
      const balances = await base44.entities.AmrBalance.filter({
        user_email: user.email
      });

      if (balances && balances.length > 0) {
        setBalance(balances[0]);
      }
    } catch (error) {
      console.error('Error loading balance:', error);
    } finally {
      setIsLoading(false);
    }
  };

  // Packs de C칩digos AMR - Pago en MXN
  const amrPacks = [
    {
      id: 'pack-150',
      name: 'Pack B치sico',
      amr_amount: 150,
      price_mxn: 150,
      description: '1 C칩digo de 150 AMR',
      popular: false
    },
    {
      id: 'pack-500',
      name: 'Pack Est치ndar',
      amr_amount: 500,
      price_mxn: 500,
      description: '1 C칩digo de 500 AMR',
      popular: false
    },
    {
      id: 'pack-1000',
      name: 'Pack Premium',
      amr_amount: 1000,
      price_mxn: 1000,
      description: '1 C칩digo de 1000 AMR',
      popular: true
    },
    {
      id: 'pack-5000',
      name: 'Pack Institucional',
      amr_amount: 5000,
      price_mxn: 5000,
      description: '1 C칩digo de 5000 AMR',
      popular: false
    }
  ];

  // Productos del Ecosistema - Pago en AMR
  const ecosystemProducts = [
    {
      id: 'gachapon-ticket',
      name: 'Ticket Gachapon Digital',
      price_amr: 50,
      description: 'Una oportunidad en la ruleta de premios ARKAIOS',
      icon: '游꿣'
    },
    {
      id: 'nft-badge',
      name: 'Insignia NFT Ciudadano',
      price_amr: 200,
      description: 'Badge digital coleccionable verificado',
      icon: '游끤'
    },
    {
      id: 'ai-credits',
      name: 'Cr칠ditos IA Premium',
      price_amr: 300,
      description: '1000 cr칠ditos para el portal de IA Soberana',
      icon: '游뱄'
    },
    {
      id: 'education-course',
      name: 'Curso Escuela Digital',
      price_amr: 500,
      description: 'Acceso a curso completo en Educaci칩n Libre',
      icon: '游닄'
    }
  ];

  const handlePurchaseWithAmr = async (product) => {
    if (!balance || balance.balance < product.price_amr) {
      setMessage({ 
        type: 'error', 
        text: `Saldo insuficiente. Necesitas ${product.price_amr} AMR` 
      });
      return;
    }

    setIsProcessing(true);
    setMessage(null);

    try {
      // Descontar AMR
      await base44.entities.AmrBalance.update(balance.id, {
        balance: balance.balance - product.price_amr
      });

      // Registrar compra
      await base44.entities.Transaction.create({
        user_email: user.email,
        type: 'purchase',
        amount: -product.price_amr,
        description: `Compra: ${product.name}`,
        status: 'completed'
      });

      setMessage({ 
        type: 'success', 
        text: `춰${product.name} adquirido exitosamente!` 
      });

      setTimeout(() => {
        loadBalance();
        setSelectedProduct(null);
      }, 2000);
    } catch (error) {
      console.error('Error purchasing product:', error);
      setMessage({ type: 'error', text: 'Error al procesar la compra' });
    } finally {
      setIsProcessing(false);
    }
  };

  const handleBuyAmrPack = (pack) => {
    setMessage({ 
      type: 'info', 
      text: `Redirigiendo a pasarela de pago MXN para ${pack.name}...` 
    });
    // Aqu칤 ir칤a la integraci칩n con la pasarela de pago real
    // Por ahora simulamos el proceso
    alert(`PASARELA DE PAGO: ${pack.name} - $${pack.price_mxn} MXN\n\nEsto abrir칤a la pasarela de pago real.`);
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center p-12">
        <Loader2 className="w-8 h-8 text-purple-400 animate-spin" />
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Saldo Actual */}
      <Card className="bg-gradient-to-r from-cyan-900/30 to-blue-900/30 border-cyan-500/50">
        <CardContent className="p-6">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-3">
              <Coins className="w-8 h-8 text-cyan-400" />
              <div>
                <p className="text-cyan-300 text-sm">Tu Saldo AMR</p>
                <p className="text-3xl font-bold text-white">
                  {balance ? balance.balance.toFixed(2) : '0.00'} AMR
                </p>
              </div>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Mercado */}
      <Tabs defaultValue="packs" className="w-full">
        <TabsList className="grid w-full grid-cols-3 bg-slate-900/50">
          <TabsTrigger value="packs" className="data-[state=active]:bg-green-600">
            <CreditCard className="w-4 h-4 mr-2" />
            Packs AMR
          </TabsTrigger>
          <TabsTrigger value="ecosystem" className="data-[state=active]:bg-purple-600">
            <ShoppingBag className="w-4 h-4 mr-2" />
            Ecosistema
          </TabsTrigger>
          <TabsTrigger value="membership" className="data-[state=active]:bg-yellow-600">
            <Sparkles className="w-4 h-4 mr-2" />
            Membres칤as
          </TabsTrigger>
        </TabsList>

        {/* Packs de AMR - Pago en MXN */}
        <TabsContent value="packs" className="mt-6 space-y-4">
          <div className="bg-green-900/20 border border-green-500/50 p-4 rounded-lg mb-4">
            <p className="text-green-300 text-sm flex items-center gap-2">
              <CreditCard className="w-4 h-4" />
              Estos packs se compran con MXN (pesos mexicanos) mediante pasarela de pago
            </p>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {amrPacks.map((pack) => (
              <Card 
                key={pack.id} 
                className={`bg-slate-900/50 ${
                  pack.popular ? 'border-yellow-500/50 ring-2 ring-yellow-500/30' : 'border-slate-700'
                }`}
              >
                <CardContent className="p-6">
                  {pack.popular && (
                    <div className="inline-block px-2 py-1 bg-yellow-500/20 border border-yellow-500/50 rounded-full mb-3">
                      <span className="text-yellow-300 text-xs font-bold">POPULAR</span>
                    </div>
                  )}
                  <h3 className="text-xl font-bold text-white mb-2">{pack.name}</h3>
                  <p className="text-slate-400 text-sm mb-4">{pack.description}</p>
                  <div className="flex items-baseline gap-2 mb-4">
                    <p className="text-3xl font-bold text-green-400">${pack.price_mxn}</p>
                    <p className="text-slate-400">MXN</p>
                  </div>
                  <p className="text-cyan-300 mb-4">= {pack.amr_amount} AMR</p>
                  <Button
                    onClick={() => handleBuyAmrPack(pack)}
                    className="w-full bg-gradient-to-r from-green-600 to-emerald-600 hover:from-green-500 hover:to-emerald-500"
                  >
                    <CreditCard className="w-4 h-4 mr-2" />
                    Comprar con MXN
                  </Button>
                </CardContent>
              </Card>
            ))}
          </div>
        </TabsContent>

        {/* Productos Ecosistema - Pago en AMR */}
        <TabsContent value="ecosystem" className="mt-6 space-y-4">
          <div className="bg-purple-900/20 border border-purple-500/50 p-4 rounded-lg mb-4">
            <p className="text-purple-300 text-sm flex items-center gap-2">
              <Coins className="w-4 h-4" />
              Estos productos se compran con AMR de tu saldo
            </p>
          </div>

          {message && (
            <div className={`flex items-center gap-2 p-3 rounded-lg ${
              message.type === 'success' 
                ? 'bg-green-500/20 border border-green-500/50 text-green-300'
                : message.type === 'info'
                ? 'bg-blue-500/20 border border-blue-500/50 text-blue-300'
                : 'bg-red-500/20 border border-red-500/50 text-red-300'
            }`}>
              {message.type === 'success' ? (
                <CheckCircle2 className="w-5 h-5" />
              ) : (
                <AlertCircle className="w-5 h-5" />
              )}
              <span>{message.text}</span>
            </div>
          )}

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {ecosystemProducts.map((product) => (
              <Card key={product.id} className="bg-slate-900/50 border-purple-500/30">
                <CardContent className="p-6">
                  <div className="text-4xl mb-3">{product.icon}</div>
                  <h3 className="text-lg font-bold text-white mb-2">{product.name}</h3>
                  <p className="text-slate-400 text-sm mb-4">{product.description}</p>
                  <div className="flex items-baseline gap-2 mb-4">
                    <p className="text-2xl font-bold text-purple-400">{product.price_amr}</p>
                    <p className="text-slate-400">AMR</p>
                  </div>
                  <Button
                    onClick={() => handlePurchaseWithAmr(product)}
                    disabled={isProcessing || !balance || balance.balance < product.price_amr}
                    className="w-full bg-gradient-to-r from-purple-600 to-pink-600 hover:from-purple-500 hover:to-pink-500 disabled:opacity-50"
                  >
                    {isProcessing ? (
                      <>
                        <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                        Procesando...
                      </>
                    ) : (
                      <>
                        <Coins className="w-4 h-4 mr-2" />
                        Comprar con AMR
                      </>
                    )}
                  </Button>
                  {balance && balance.balance < product.price_amr && (
                    <p className="text-red-400 text-xs mt-2 text-center">
                      Saldo insuficiente
                    </p>
                  )}
                </CardContent>
              </Card>
            ))}
          </div>

          {(!balance || balance.balance < 50) && (
            <Card className="bg-yellow-900/20 border-yellow-500/50">
              <CardContent className="p-6 text-center">
                <p className="text-yellow-300 mb-3">
                  쯅o tienes suficiente AMR? Compra un pack en la pesta침a "Packs AMR"
                </p>
                <Button
                  onClick={() => document.querySelector('[value="packs"]').click()}
                  variant="outline"
                  className="border-yellow-500/50 text-yellow-300 hover:bg-yellow-500/20"
                >
                  Ver Packs AMR
                </Button>
              </CardContent>
            </Card>
          )}
        </TabsContent>

        {/* Membres칤as - Pago en MXN */}
        <TabsContent value="membership" className="mt-6">
          <div className="bg-yellow-900/20 border border-yellow-500/50 p-4 rounded-lg mb-4">
            <p className="text-yellow-300 text-sm flex items-center gap-2">
              <CreditCard className="w-4 h-4" />
              Las membres칤as se compran con MXN mediante pasarela de pago
            </p>
          </div>

          <MembershipPurchase 
            user={user}
            selectedPlan={{
              id: 'monthly',
              name: 'Mensual',
              price: 150,
              period: '1 mes'
            }}
            onCancel={onClose}
            onSuccess={() => {
              setMessage({ type: 'success', text: '춰Membres칤a activada!' });
              setTimeout(() => onClose(), 2000);
            }}
          />
        </TabsContent>
      </Tabs>
    </div>
  );
}

