{"file_path": "components/arkaios/ArkaiosKeyGenerator.jsx", "content": "import React from 'react';
import CryptoJS from 'crypto-js';
import { Download } from 'lucide-react';
import { Button } from '@/components/ui/button';

const MASTER_KEY = "ARKAIOS_SECURE_KEY_2023!@#";

export default function ArkaiosKeyGenerator({ userData }) {
  const generateArkaiosKey = () => {
    const timestamp = new Date().toISOString();
    const walletAddress = userData.wallet_address || `ARK-${userData.email.split('@')[0].toUpperCase()}`;

    const identityData = {
      version: "2.5",
      protocol: "BITCOINLAND-BIFROST",
      identity: walletAddress,
      issued: timestamp,
      signature: btoa(`ARKAIOS-SIG-${walletAddress}-${Date.now()}`),
      financials: {
        tarjeta_16_digitos: userData.card_number_16 || "",
        clabe_interbancaria: userData.clabe_interbancaria || "",
        banco_receptor: userData.banco_receptor || ""
      }
    };

    const stringData = JSON.stringify(identityData, null, 2);
    const encryptedData = CryptoJS.AES.encrypt(stringData, MASTER_KEY).toString();

    return { identityData, encryptedData };
  };

  const downloadArkaiosKey = () => {
    const { encryptedData } = generateArkaiosKey();
    const blob = new Blob([encryptedData], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = `arkaios-key-${Date.now()}.arkaios`;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
  };

  return (
    <Button
      onClick={downloadArkaiosKey}
      className="bg-gradient-to-r from-purple-600 to-cyan-600 hover:from-purple-500 hover:to-cyan-500"
    >
      <Download className="w-4 h-4 mr-2" />
      Descargar Llave .arkaios
    </Button>
  );
}
"}

