{"file_path": "components/arkaios/TransferSystem.jsx", "content": "import React, { useState } from 'react';
import { base44 } from '@/api/base44Client';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Send, Loader2, CheckCircle2, XCircle } from 'lucide-react';

export default function TransferSystem({ user, currentBalance, onTransferComplete }) {
  const [recipientEmail, setRecipientEmail] = useState('');
  const [amount, setAmount] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);
  const [message, setMessage] = useState(null);

  const handleTransfer = async (e) => {
    e.preventDefault();
    setMessage(null);

    // Validaciones b치sicas
    if (!recipientEmail || !amount) {
      setMessage({ type: 'error', text: 'Por favor completa todos los campos' });
      return;
    }

    const transferAmount = parseFloat(amount);
    if (isNaN(transferAmount) || transferAmount <= 0) {
      setMessage({ type: 'error', text: 'Monto inv치lido' });
      return;
    }

    if (transferAmount > currentBalance) {
      setMessage({ type: 'error', text: 'Saldo insuficiente' });
      return;
    }

    if (recipientEmail === user.email) {
      setMessage({ type: 'error', text: 'No puedes transferirte a ti mismo' });
      return;
    }

    setIsProcessing(true);

    try {
      // Verificar que el destinatario existe
      const recipientBalances = await base44.entities.AmrBalance.filter({
        user_email: recipientEmail
      });

      if (!recipientBalances || recipientBalances.length === 0) {
        setMessage({ type: 'error', text: 'El destinatario no tiene una cuenta AMR' });
        setIsProcessing(false);
        return;
      }

      const recipientBalance = recipientBalances[0];

      // Obtener balance del emisor
      const senderBalances = await base44.entities.AmrBalance.filter({
        user_email: user.email
      });

      if (!senderBalances || senderBalances.length === 0) {
        setMessage({ type: 'error', text: 'No tienes una cuenta AMR' });
        setIsProcessing(false);
        return;
      }

      const senderBalance = senderBalances[0];

      // Verificaci칩n final de saldo
      if (senderBalance.balance < transferAmount) {
        setMessage({ type: 'error', text: 'Saldo insuficiente' });
        setIsProcessing(false);
        return;
      }

      // Crear transacci칩n de env칤o
      await base44.entities.Transaction.create({
        user_email: user.email,
        type: 'transfer_sent',
        amount: -transferAmount,
        description: `Transferencia enviada a ${recipientEmail}`,
        status: 'completed',
        recipient_email: recipientEmail
      });

      // Crear transacci칩n de recepci칩n
      await base44.entities.Transaction.create({
        user_email: recipientEmail,
        type: 'transfer_received',
        amount: transferAmount,
        description: `Transferencia recibida de ${user.email}`,
        status: 'completed',
        reference: user.email
      });

      // Actualizar balance del emisor
      await base44.entities.AmrBalance.update(senderBalance.id, {
        balance: senderBalance.balance - transferAmount
      });

      // Actualizar balance del receptor
      await base44.entities.AmrBalance.update(recipientBalance.id, {
        balance: recipientBalance.balance + transferAmount
      });

      setMessage({ 
        type: 'success', 
        text: `Transferencia de ${transferAmount} AMR enviada exitosamente` 
      });
      setRecipientEmail('');
      setAmount('');

      // Notificar al componente padre
      if (onTransferComplete) {
        onTransferComplete();
      }

    } catch (error) {
      console.error('Error en transferencia:', error);
      setMessage({ type: 'error', text: 'Error al procesar la transferencia' });
    } finally {
      setIsProcessing(false);
    }
  };

  return (
    <Card className="bg-slate-900/50 border-cyan-500/30">
      <CardContent className="p-6">
        <div className="flex items-center gap-3 mb-4">
          <Send className="w-6 h-6 text-cyan-400" />
          <h3 className="text-xl font-bold text-white">Transferencias P2P</h3>
        </div>

        <form onSubmit={handleTransfer} className="space-y-4">
          <div>
            <Label className="text-slate-300">Email del destinatario</Label>
            <Input
              type="email"
              value={recipientEmail}
              onChange={(e) => setRecipientEmail(e.target.value)}
              placeholder="usuario@ejemplo.com"
              className="bg-slate-800/50 border-cyan-500/30 text-white mt-1"
              disabled={isProcessing}
            />
          </div>

          <div>
            <Label className="text-slate-300">Monto (AMR)</Label>
            <Input
              type="number"
              step="0.01"
              value={amount}
              onChange={(e) => setAmount(e.target.value)}
              placeholder="0.00"
              className="bg-slate-800/50 border-cyan-500/30 text-white mt-1"
              disabled={isProcessing}
            />
            <p className="text-xs text-slate-500 mt-1">
              Saldo disponible: {currentBalance} AMR
            </p>
          </div>

          <Button
            type="submit"
            disabled={isProcessing}
            className="w-full bg-gradient-to-r from-cyan-600 to-blue-600 hover:from-cyan-500 hover:to-blue-500"
          >
            {isProcessing ? (
              <>
                <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                Procesando...
              </>
            ) : (
              <>
                <Send className="w-4 h-4 mr-2" />
                Enviar Transferencia
              </>
            )}
          </Button>

          {message && (
            <div className={`flex items-center gap-2 p-3 rounded-lg ${
              message.type === 'success' 
                ? 'bg-green-900/30 border border-green-500/50' 
                : 'bg-red-900/30 border border-red-500/50'
            }`}>
              {message.type === 'success' ? (
                <CheckCircle2 className="w-5 h-5 text-green-400" />
              ) : (
                <XCircle className="w-5 h-5 text-red-400" />
              )}
              <p className={message.type === 'success' ? 'text-green-300' : 'text-red-300'}>
                {message.text}
              </p>
            </div>
          )}
        </form>
      </CardContent>
    </Card>
  );
}
"}

