import { base44 } from '@/api/base44Client';
import { Card, CardContent } from '@/components/ui/card';
import { ScrollArea } from '@/components/ui/scroll-area';
import { History, ArrowUpRight, ArrowDownLeft, Gift, CreditCard, Loader2 } from 'lucide-react';
import { format } from 'date-fns';

export default function TransactionHistory({ user }) {
  const [transactions, setTransactions] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    loadTransactions();
  }, [user]);

  const loadTransactions = async () => {
    setIsLoading(true);
    try {
      const userTransactions = await base44.entities.Transaction.filter({
        user_email: user.email
      }, '-created_date', 50);

      setTransactions(userTransactions || []);
    } catch (error) {
      console.error('Error loading transactions:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const getTransactionIcon = (type) => {
    switch (type) {
      case 'transfer_sent':
        return <ArrowUpRight className="w-5 h-5 text-red-400" />;
      case 'transfer_received':
        return <ArrowDownLeft className="w-5 h-5 text-green-400" />;
      case 'code_redeem':
        return <Gift className="w-5 h-5 text-purple-400" />;
      case 'membership_payment':
        return <CreditCard className="w-5 h-5 text-yellow-400" />;
      default:
        return <History className="w-5 h-5 text-cyan-400" />;
    }
  };

  const getTransactionColor = (type) => {
    switch (type) {
      case 'transfer_sent':
      case 'membership_payment':
        return 'text-red-400';
      case 'transfer_received':
      case 'code_redeem':
        return 'text-green-400';
      default:
        return 'text-slate-400';
    }
  };

  if (isLoading) {
    return (
      <Card className="bg-slate-900/50 border-purple-500/30">
        <CardContent className="p-6">
          <div className="flex items-center justify-center py-8">
            <Loader2 className="w-8 h-8 text-purple-400 animate-spin" />
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className="bg-slate-900/50 border-purple-500/30">
      <CardContent className="p-6">
        <div className="flex items-center gap-3 mb-4">
          <History className="w-6 h-6 text-purple-400" />
          <h3 className="text-xl font-bold text-white">Historial de Transacciones</h3>
        </div>

        {transactions.length === 0 ? (
          <div className="text-center py-8">
            <p className="text-slate-400">No hay transacciones registradas</p>
          </div>
        ) : (
          <ScrollArea className="h-[400px] pr-4">
            <div className="space-y-3">
              {transactions.map((transaction) => (
                <div
                  key={transaction.id}
                  className="bg-slate-800/50 rounded-lg p-4 border border-slate-700/50 hover:border-purple-500/50 transition-colors"
                >
                  <div className="flex items-start justify-between">
                    <div className="flex items-start gap-3">
                      <div className="mt-1">
                        {getTransactionIcon(transaction.type)}
                      </div>
                      <div>
                        <p className="text-white font-medium">
                          {transaction.description || transaction.type}
                        </p>
                        <p className="text-xs text-slate-500 mt-1">
                          {format(new Date(transaction.created_date), 'dd/MM/yyyy HH:mm:ss')}
                        </p>
                        {transaction.recipient_email && (
                          <p className="text-xs text-slate-400 mt-1">
                            Para: {transaction.recipient_email}
                          </p>
                        )}
                        {transaction.reference && (
                          <p className="text-xs text-slate-400 mt-1">
                            De: {transaction.reference}
                          </p>
                        )}
                      </div>
                    </div>
                    <div className="text-right">
                      <p className={`text-lg font-bold ${getTransactionColor(transaction.type)}`}>
                        {transaction.amount > 0 ? '+' : ''}{transaction.amount} AMR
                      </p>
                      <span className={`text-xs px-2 py-1 rounded-full ${
                        transaction.status === 'completed' 
                          ? 'bg-green-900/30 text-green-400' 
                          : transaction.status === 'pending'
                          ? 'bg-yellow-900/30 text-yellow-400'
                          : 'bg-red-900/30 text-red-400'
                      }`}>
                        {transaction.status}
                      </span>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </ScrollArea>
        )}
      </CardContent>
    </Card>
  );
}
"}

