{"file_path": "components/arkaios/MembershipPurchase.jsx", "content": "import React, { useState } from 'react';
import { base44 } from '@/api/base44Client';
import { Button } from '@/components/ui/button';
import { Card, CardContent } from '@/components/ui/card';
import { CheckCircle2, XCircle, Loader2, AlertTriangle } from 'lucide-react';

export default function MembershipPurchase({ user, plan, onSuccess, onCancel }) {
  const [isProcessing, setIsProcessing] = useState(false);
  const [message, setMessage] = useState(null);

  const calculateEndDate = (planType) => {
    const now = new Date();
    switch (planType) {
      case 'monthly':
        return new Date(now.setMonth(now.getMonth() + 1));
      case 'quarterly':
        return new Date(now.setMonth(now.getMonth() + 3));
      case 'biannual':
        return new Date(now.setMonth(now.getMonth() + 6));
      case 'annual':
        return new Date(now.setFullYear(now.getFullYear() + 1));
      default:
        return new Date(now.setMonth(now.getMonth() + 1));
    }
  };

  const handlePurchase = async () => {
    setIsProcessing(true);
    setMessage(null);

    try {
      // 1. Verificar balance del usuario
      const balances = await base44.entities.AmrBalance.filter({
        user_email: user.email
      });

      if (!balances || balances.length === 0) {
        setMessage({ 
          type: 'error', 
          text: 'No tienes una cuenta AMR. Por favor, canjea un c칩digo primero.' 
        });
        setIsProcessing(false);
        return;
      }

      const userBalance = balances[0];

      // 2. Validar saldo suficiente
      if (userBalance.balance < plan.price) {
        setMessage({ 
          type: 'error', 
          text: `Saldo insuficiente. Tienes ${userBalance.balance} AMR, necesitas ${plan.price} AMR.` 
        });
        setIsProcessing(false);
        return;
      }

      // 3. Verificar si ya tiene membres칤a activa
      const existingMemberships = await base44.entities.Membership.filter({
        user_email: user.email,
        status: 'active'
      });

      if (existingMemberships && existingMemberships.length > 0) {
        const activeMembership = existingMemberships[0];
        const endDate = new Date(activeMembership.end_date);
        const now = new Date();

        if (endDate > now) {
          setMessage({ 
            type: 'error', 
            text: 'Ya tienes una membres칤a activa. Espera a que expire para comprar otra.' 
          });
          setIsProcessing(false);
          return;
        }
      }

      // 4. Deducir del balance
      const newBalance = userBalance.balance - plan.price;
      await base44.entities.AmrBalance.update(userBalance.id, {
        balance: newBalance
      });

      // 5. Crear registro de transacci칩n
      const transaction = await base44.entities.Transaction.create({
        user_email: user.email,
        type: 'membership_payment',
        amount: -plan.price,
        description: `Compra de membres칤a ${plan.name}`,
        status: 'completed',
        reference: plan.id
      });

      // 6. Crear o actualizar membres칤a
      const startDate = new Date();
      const endDate = calculateEndDate(plan.id);

      if (existingMemberships && existingMemberships.length > 0) {
        // Actualizar membres칤a existente
        await base44.entities.Membership.update(existingMemberships[0].id, {
          plan_type: plan.id,
          start_date: startDate.toISOString(),
          end_date: endDate.toISOString(),
          status: 'active',
          amount_paid: plan.price,
          transaction_id: transaction.id
        });
      } else {
        // Crear nueva membres칤a
        await base44.entities.Membership.create({
          user_email: user.email,
          plan_type: plan.id,
          start_date: startDate.toISOString(),
          end_date: endDate.toISOString(),
          status: 'active',
          amount_paid: plan.price,
          transaction_id: transaction.id
        });
      }

      setMessage({ 
        type: 'success', 
        text: `춰Membres칤a ${plan.name} activada exitosamente! Ya puedes descargar tu Pasaporte Ciudadano.` 
      });

      // Esperar un momento para que el usuario vea el mensaje
      setTimeout(() => {
        if (onSuccess) onSuccess();
      }, 2000);

    } catch (error) {
      console.error('Error al procesar la compra:', error);
      setMessage({ 
        type: 'error', 
        text: 'Error al procesar la compra. Por favor intenta de nuevo.' 
      });
    } finally {
      setIsProcessing(false);
    }
  };

  return (
    <Card className="bg-slate-900/50 border-cyan-500/30">
      <CardContent className="p-6">
        <div className="text-center space-y-4">
          <h3 className="text-2xl font-bold text-white">
            Confirmar Compra
          </h3>
          <div className="bg-slate-800/50 rounded-lg p-6">
            <p className="text-slate-300 mb-2">Plan seleccionado:</p>
            <p className="text-3xl font-bold text-transparent bg-gradient-to-r from-purple-400 to-cyan-400 bg-clip-text mb-1">
              {plan.name}
            </p>
            <p className="text-slate-400 text-sm mb-4">{plan.description}</p>
            <div className="flex items-center justify-center gap-2 text-2xl font-bold text-yellow-400">
              <span>{plan.price}</span>
              <span className="text-lg text-slate-400">AMR</span>
            </div>
          </div>

          {message && (
            <div className={`flex items-center gap-2 p-4 rounded-lg ${
              message.type === 'success' 
                ? 'bg-green-900/30 border border-green-500/50' 
                : 'bg-red-900/30 border border-red-500/50'
            }`}>
              {message.type === 'success' ? (
                <CheckCircle2 className="w-6 h-6 text-green-400 flex-shrink-0" />
              ) : (
                <XCircle className="w-6 h-6 text-red-400 flex-shrink-0" />
              )}
              <p className={`text-sm ${message.type === 'success' ? 'text-green-300' : 'text-red-300'}`}>
                {message.text}
              </p>
            </div>
          )}

          <div className="flex gap-3">
            <Button
              onClick={onCancel}
              variant="outline"
              className="flex-1 border-slate-600 text-slate-300"
              disabled={isProcessing}
            >
              Cancelar
            </Button>
            <Button
              onClick={handlePurchase}
              className="flex-1 bg-gradient-to-r from-purple-600 to-cyan-600 hover:from-purple-500 hover:to-cyan-500"
              disabled={isProcessing}
            >
              {isProcessing ? (
                <>
                  <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                  Procesando...
                </>
              ) : (
                <>
                  <CheckCircle2 className="w-4 h-4 mr-2" />
                  Confirmar Compra
                </>
              )}
            </Button>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}
"}

