{"file_path": "components/arkaios/PortalViewer.jsx", "content": "import React from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { X, ExternalLink } from 'lucide-react';
import { Button } from '@/components/ui/button';

export default function PortalViewer({ isOpen, onClose, portalUrl, portalTitle }) {
  if (!isOpen) return null;

  return (
    <AnimatePresence>
      <div className="fixed inset-0 z-50 flex items-center justify-center">
        {/* Backdrop */}
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          onClick={onClose}
          className="absolute inset-0 bg-black/90 backdrop-blur-sm"
        />

        {/* Portal Container */}
        <motion.div
          initial={{ opacity: 0, scale: 0.95 }}
          animate={{ opacity: 1, scale: 1 }}
          exit={{ opacity: 0, scale: 0.95 }}
          className="relative w-[95vw] h-[95vh] bg-slate-900 rounded-2xl border-2 border-purple-500/50 shadow-2xl overflow-hidden flex flex-col"
        >
          {/* Header */}
          <div className="bg-slate-900/95 backdrop-blur-sm border-b border-purple-500/30 p-4 flex items-center justify-between flex-shrink-0">
            <div className="flex items-center gap-3">
              <div className="w-3 h-3 rounded-full bg-red-500"></div>
              <div className="w-3 h-3 rounded-full bg-yellow-500"></div>
              <div className="w-3 h-3 rounded-full bg-green-500"></div>
              <span className="ml-4 text-white font-semibold">{portalTitle}</span>
            </div>
            <Button
              variant="ghost"
              size="icon"
              onClick={onClose}
              className="text-slate-400 hover:text-white hover:bg-slate-800"
            >
              <X className="w-5 h-5" />
            </Button>
          </div>

          {/* Iframe Content */}
          <div className="flex-1 relative bg-white">
            <iframe
              src={portalUrl}
              className="w-full h-full border-0"
              title={portalTitle}
              sandbox="allow-same-origin allow-scripts allow-forms allow-popups allow-popups-to-escape-sandbox"
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
            />
          </div>

          {/* Footer Badge */}
          <div className="bg-slate-900/95 border-t border-purple-500/30 px-4 py-2 flex items-center justify-center flex-shrink-0">
            <span className="text-xs text-slate-500">
              Powered by ARKAIOS Ecosystem  Conexi칩n Segura Encriptada
            </span>
          </div>
        </motion.div>
      </div>
    </AnimatePresence>
  );
}
"}

